MATCH (:ActivityInstanceRoot)-[LATEST]->(av:ActivityInstnaceValue)
WHERE not exists {(aiv)-[:HAS_GROUPING]->(:ActivityGrouping)}
RETURN count(av)

MATCH (:ActivityInstanceRoot)-[LATEST]->(aiv:ActivityInstnaceValue)
WHERE not exists {(aiv)-[:HAS_ACTIVITY]->(:ActivityGrouping)}
RETURN count(aiv)

MATCH (:ActivityInstanceRoot)-[LATEST]->(av:ActivityInstnaceValue)
WHERE not exists {(aiv)-[:HAS_GROUPING]->(:ActivityGrouping)}
RETURN av.name AS `Activity`

MATCH (:ActivityInstanceRoot)-[LATEST]->(aiv:ActivityInstnaceValue)
WHERE not exists {(aiv)-[:HAS_ACTIVITY]->(:ActivityGrouping)}
RETURN aiv.name AS `Activity Instance`

match (ar:ActivityRoot)-[]-(av:ActivityValue)--(n:ActivityGrouping)--(aiv:ActivityInstanceValue)<-[:LATEST]-(air:ActivityInstanceRoot)
where not exists {()-[:LATEST]->(:ActivityValue)--(n)}
return av.name as Activity, n.uid as `Grouping UID`, collect(aiv.name) as `Activity Instances` order by `Grouping UID`
